'begin',
'	for objs in ( select VERSION_ID from AMB_OBJECT where ID=f_object_id)',
'	loop',
'		IF f_version_id IS NULL OR objs.VERSION_ID = f_version_id THEN',
'			v_validate:=TRUE;',
'		END IF;',
'		EXIT;',
'	end loop;',
'	return v_validate;',
'end;',
